// Technical Indicators Calculator
// Berechne SMA, RSI, MACD für alle Symbole

const bars = $input.json;
const indicators = {};

function calculateSMA(prices, period) {
  if (prices.length < period) return null;
  const sum = prices.slice(-period).reduce((a, b) => a + b, 0);
  return sum / period;
}

function calculateRSI(prices, period = 14) {
  if (prices.length < period + 1) return null;

  let gains = 0, losses = 0;
  for (let i = prices.length - period; i < prices.length; i++) {
    const change = prices[i] - prices[i - 1];
    if (change > 0) gains += change;
    else losses -= change;
  }

  const avgGain = gains / period;
  const avgLoss = losses / period;
  const rs = avgLoss === 0 ? 100 : avgGain / avgLoss;
  return 100 - (100 / (1 + rs));
}

function calculateATR(highs, lows, closes, period = 14) {
  if (highs.length < period) return null;

  let trueRanges = [];
  for (let i = 1; i < highs.length; i++) {
    const tr = Math.max(
      highs[i] - lows[i],
      Math.abs(highs[i] - closes[i - 1]),
      Math.abs(lows[i] - closes[i - 1])
    );
    trueRanges.push(tr);
  }

  const atr = trueRanges.slice(-period).reduce((a, b) => a + b, 0) / period;
  return atr;
}

// Process each symbol
for (const [symbol, barData] of Object.entries(bars.bars || {})) {
  if (!barData || !Array.isArray(barData)) continue;

  const closes = barData.map(b => b.c);
  const highs = barData.map(b => b.h);
  const lows = barData.map(b => b.l);
  const volumes = barData.map(b => b.v);

  const currentPrice = closes[closes.length - 1];
  const prevClose = closes[closes.length - 2];

  indicators[symbol] = {
    symbol: symbol,
    current_price: currentPrice,
    prev_close: prevClose,
    change_pct: ((currentPrice - prevClose) / prevClose * 100).toFixed(2),
    sma_20: (calculateSMA(closes, 20) || 0).toFixed(2),
    sma_50: (calculateSMA(closes, 50) || 0).toFixed(2),
    sma_200: (calculateSMA(closes, 200) || 0).toFixed(2),
    rsi_14: (calculateRSI(closes, 14) || 0).toFixed(2),
    atr_14: (calculateATR(highs, lows, closes, 14) || 0).toFixed(2),
    volume_current: volumes[volumes.length - 1],
    volume_avg_20: (volumes.slice(-20).reduce((a, b) => a + b, 0) / 20).toFixed(0)
  };
}

return [{
  json: {
    indicators: indicators,
    timestamp: new Date().toISOString()
  }
}];

// Risk Management Calculator
// Position Size, Stop-Loss, Take-Profit Levels

const account = $node["alpaca-account-info"].json;
const indicators = $input.json.indicators;
const config = $node["config-setup"].json;
const positions = $node["alpaca-get-positions"].json;

const portfolio = {
  total_cash: parseFloat(account.cash) || 0,
  buying_power: parseFloat(account.buying_power) || 0,
  total_portfolio_value: parseFloat(account.portfolio_value) || 0,
  current_positions: positions.length || 0
};

const riskAnalysis = {};
const positionSizePct = parseFloat(config.position_size_pct) || 0.03;
const maxPositionSize = portfolio.total_portfolio_value * positionSizePct;

for (const [symbol, ind] of Object.entries(indicators)) {
  const currentPrice = parseFloat(ind.current_price);
  const atr = parseFloat(ind.atr_14) || currentPrice * 0.02;
  const rsi = parseFloat(ind.rsi_14);

  // Risk/Reward Setup
  const stopLossDistance = atr * 1.5; // 1.5x ATR below entry
  const takeProfitDistance = stopLossDistance * 2; // 2:1 Risk/Reward

  const entryPrice = currentPrice;
  const stopLoss = (entryPrice - stopLossDistance).toFixed(2);
  const takeProfit = (entryPrice + takeProfitDistance).toFixed(2);

  // Position Size Calculation
  const sharesForMaxRisk = Math.floor(maxPositionSize / currentPrice);

  riskAnalysis[symbol] = {
    symbol: symbol,
    current_price: currentPrice.toFixed(2),
    entry_price: entryPrice.toFixed(2),
    stop_loss: stopLoss,
    take_profit: takeProfit,
    risk_per_share: stopLossDistance.toFixed(2),
    reward_per_share: takeProfitDistance.toFixed(2),
    suggested_shares: sharesForMaxRisk,
    position_value: (sharesForMaxRisk * currentPrice).toFixed(2),
    rsi: rsi.toFixed(0),
    rsi_signal: rsi < 30 ? 'oversold' : rsi > 70 ? 'overbought' : 'neutral'
  };
}

return [{
  json: {
    portfolio: portfolio,
    risk_analysis: riskAnalysis,
    available_for_trading: portfolio.buying_power,
    timestamp: new Date().toISOString()
  }
}];

// Trade Signal Validator & Order Builder
const signals = $input.json.signals.signals;
const riskData = $node["calc-risk"].json.risk_analysis;
const config = $node["config-setup"].json;
const minConfidence = parseInt(config.min_confidence) || 70;
const tradingEnabled = config.trading_enabled === 'true';
const paperTrading = config.paper_trading === 'true';

const validOrders = [];
const rejectedSignals = [];

for (const [symbol, signal] of Object.entries(signals)) {
  const risk = riskData[symbol];

  // Validate signal
  if (!signal.signal || !signal.confidence) {
    rejectedSignals.push({
      symbol: symbol,
      reason: 'Missing signal data'
    });
    continue;
  }

  const confidence = parseInt(signal.confidence);

  if (confidence < minConfidence) {
    rejectedSignals.push({
      symbol: symbol,
      signal: signal.signal,
      confidence: confidence,
      reason: `Confidence ${confidence} below minimum ${minConfidence}`
    });
    continue;
  }

  // Build order if signal is BUY and confidence passes
  if (signal.signal === 'BUY' && confidence >= minConfidence) {
    validOrders.push({
      symbol: symbol,
      side: 'buy',
      qty: parseInt(risk.suggested_shares) || 1,
      type: 'limit',
      limit_price: parseFloat(risk.current_price),
      stop_price: parseFloat(risk.stop_loss),
      take_profit_price: parseFloat(risk.take_profit),
      confidence: confidence,
      setup: signal.entry_setup,
      time_in_force: 'day',
      should_execute: tradingEnabled && confidence >= 75
    });
  }
  // SELL signal processing
  else if (signal.signal === 'SELL' && confidence >= minConfidence) {
    validOrders.push({
      symbol: symbol,
      side: 'sell',
      type: 'market',
      confidence: confidence,
      setup: signal.entry_setup,
      time_in_force: 'day',
      should_execute: tradingEnabled && confidence >= 75
    });
  }
}

return [{
  json: {
    valid_orders: validOrders,
    rejected_signals: rejectedSignals,
    paper_trading: paperTrading,
    trading_enabled: tradingEnabled,
    total_valid_signals: validOrders.length,
    total_rejected: rejectedSignals.length,
    timestamp: new Date().toISOString()
  }
}];

// Order Result Handler & Logger
const order = $input.json;
const symbol = order.symbol;
const orderId = order.id;

const result = {
  execution_status: order.id ? 'SUCCESS' : 'FAILED',
  order_id: orderId,
  symbol: symbol,
  side: order.side,
  qty: order.qty,
  filled_qty: order.filled_qty || 0,
  filled_avg_price: order.filled_avg_price || null,
  limit_price: order.limit_price,
  status: order.status,
  created_at: order.created_at,
  updated_at: order.updated_at,
  message: orderId ? `Order ${orderId} submitted for ${symbol}` : 'Order placement failed'
};

return [{
  json: result
}];
